    private void compareCommits() {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.commit.CommitCompareListFragment.compareCommits()",this);ServiceGenerator.createService(getActivity(), RepositoryCommitService.class)
                .compareCommits(repository.owner().login(), repository.name(), base, head)
                .subscribeOn(Schedulers.io())
                .observeOn(AndroidSchedulers.mainThread())
                .as(AutoDisposeUtils.bindToLifecycle(this))
                .subscribe(response -> {
                    CommitCompare compareCommit = response.body();
                    List<GitHubFile> files = compareCommit.files();
                    diffStyler.setFiles(files);
                    Collections.sort(files, new CommitFileComparator());
                    updateList(compareCommit);
                }, error -> ToastUtils.show(getActivity(), R.string.error_commits_load));com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.commit.CommitCompareListFragment.compareCommits()",this);
    }

    private void updateList(CommitCompare compare) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.commit.CommitCompareListFragment.updateList(com.meisolsson.githubsdk.model.CommitCompare)",this);if (!isAdded()) {
            {com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.commit.CommitCompareListFragment.updateList(com.meisolsson.githubsdk.model.CommitCompare)",this,1);return;}
        }

        this.compare = compare;

        progress.setVisibility(View.GONE);
        list.setVisibility(View.VISIBLE);

        List<Commit> commits = compare.commits();
        if (!commits.isEmpty()) {
            String comparingCommits = getString(R.string.comparing_commits);
            String text = MessageFormat.format(comparingCommits, commits.size());
            commitsSection.setHeader(
                    new TextItem(R.layout.commit_details_header, R.id.tv_commit_summary, text)
            );

            List<CommitItem> items = new ArrayList<>();
            for (Commit commit : commits) {
                items.add(new CommitItem(avatars, commit));
            }

            commitsSection.update(items);
        }

        List<GitHubFile> files = compare.files();
        if (!files.isEmpty()) {
            filesSection.setHeader(
                    new TextItem(R.layout.commit_compare_file_details_header,
                            R.id.tv_commit_file_summary, CommitUtils.formatStats(files))
            );
            filesSection.update(createFileSections(files));
        }
    }

    private List<Section> createFileSections(List<GitHubFile> files) {
        com.mijack.Xlog.logMethodEnter("List<Section> com.github.pockethub.android.ui.commit.CommitCompareListFragment.createFileSections(List<GitHubFile>)",this);List<Section> sections = new ArrayList<>();

        for (GitHubFile file : files) {
            Section section = new Section(new CommitFileHeaderItem(getActivity(), file));
            List<CharSequence> lines = diffStyler.get(file.filename());
            for (CharSequence line : lines) {
                section.add(new CommitFileLineItem(diffStyler, line));
            }

            sections.add(section);
        }

        {com.mijack.Xlog.logMethodExit("List<Section> com.github.pockethub.android.ui.commit.CommitCompareListFragment.createFileSections(List<GitHubFile>)",this,1);return sections;}
    }

    private void openCommit(final Commit commit) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.commit.CommitCompareListFragment.openCommit(com.meisolsson.githubsdk.model.Commit)",this);if (compare != null) {
            int commitPosition = 0;
            List<Commit> commits = compare.commits();
            for (Commit candidate : commits) {
                if (commit == candidate) {
                    break;
                } else {
                    commitPosition++;
                }
            }
            if (commitPosition < commits.size()) {
                String[] ids = new String[commits.size()];
                for (int i = 0; i < commits.size(); i++) {
                    ids[i] = commits.get(i).sha();
                }
                startActivity(CommitViewActivity.createIntent(repository, commitPosition, ids));
            }
        } else {
            startActivity(CommitViewActivity.createIntent(repository,
                    commit.sha()));
        }com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.commit.CommitCompareListFragment.openCommit(com.meisolsson.githubsdk.model.Commit)",this);
    }

