    @Override
    public void onDialogResult(int requestCode, int resultCode, Bundle arguments) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.gist.GistsViewActivity.onDialogResult(int,int,android.os.Bundle)",this);if (REQUEST_CONFIRM_DELETE == requestCode && RESULT_OK == resultCode) {
            final String gistId = arguments.getString(EXTRA_GIST_ID);

            ServiceGenerator.createService(this, GistService.class)
                    .deleteGist(gistId)
                    .subscribeOn(Schedulers.io())
