    private void updateActionBar(Gist gist, String gistId) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.gist.GistsViewActivity.updateActionBar(com.meisolsson.githubsdk.model.Gist,String)",this);ActionBar actionBar = getSupportActionBar();
        if (gist == null) {
            actionBar.setSubtitle(null);
            actionBar.setLogo(null);
            actionBar.setIcon(R.drawable.app_icon);
        } else if (gist.owner() != null) {
