/**
 * Activity to create or edit an issues filter for a repository
 */

unit.class:
public class EditIssuesFilterActivity extends BaseActivity {

    /**
     * Create intent for creating an issue filter for the given repository
     *
     * @param filter
     * @return intent
     */
    public static Intent createIntent(IssueFilter filter) {
        com.mijack.Xlog.logStaticMethodEnter("android.content.Intent com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.createIntent(com.github.pockethub.android.core.issue.IssueFilter)");com.mijack.Xlog.logStaticMethodExit("android.content.Intent com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.createIntent(com.github.pockethub.android.core.issue.IssueFilter)");return new Builder("repo.issues.filter.VIEW").add(EXTRA_ISSUE_FILTER,
            filter).toIntent();
    }

    private static final int REQUEST_LABELS = 1;

    private static final int REQUEST_MILESTONE = 2;

    private static final int REQUEST_ASSIGNEE = 3;

    @BindView(R.id.tv_labels)
    protected TextView labelsText;

    @BindView(R.id.tv_milestone)
    protected TextView milestoneText;

    @BindView(R.id.tv_assignee)
    protected TextView assigneeText;

    @BindView(R.id.iv_avatar)
    protected ImageView avatarView;

    @Inject
    protected AvatarLoader avatars;

    private LabelsDialog labelsDialog;

    private MilestoneDialog milestoneDialog;

    private AssigneeDialog assigneeDialog;

    private IssueFilter filter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onCreate(android.os.Bundle)",this);super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_issues_filter_edit);

        if (savedInstanceState != null) {
            filter = savedInstanceState.getParcelable(EXTRA_ISSUE_FILTER);
        }

        if (filter == null) {
            filter = getIntent().getParcelableExtra(EXTRA_ISSUE_FILTER);
        }

        Repository repository = filter.getRepository();

        ActionBar actionBar = getSupportActionBar();
        actionBar.setTitle(R.string.filter_issues_title);
        actionBar.setSubtitle(InfoUtils.createRepoId(repository));
        avatars.bind(actionBar, repository.owner());

        updateAssignee();
        updateMilestone();
        updateLabels();

        RadioGroup status = findViewById(R.id.issue_filter_status);
        RadioGroup sortOrder = findViewById(R.id.issue_sort_order);
        RadioGroup sortType = findViewById(R.id.issue_sort_type);

        status.setOnCheckedChangeListener(this::onStatusChanged);
        sortOrder.setOnCheckedChangeListener(this::onSortOrderChanged);
        sortType.setOnCheckedChangeListener(this::onSortTypeChanged);

        if (filter.isOpen()) {
            status.check(R.id.rb_open);
        } else {
            status.check(R.id.rb_closed);
        }


        if (filter.getDirection().equals(IssueFilter.DIRECTION_ASCENDING)) {
            sortOrder.check(R.id.rb_asc);
        } else {
            sortOrder.check(R.id.rb_desc);
        }

        switch (filter.getSortType()) {
            case IssueFilter.SORT_CREATED:
                sortType.check(R.id.rb_created);
                break;
            case IssueFilter.SORT_UPDATED:
                sortType.check(R.id.rb_updated);
                break;
            case IssueFilter.SORT_COMMENTS:
                sortType.check(R.id.rb_comments);
                break;
            default:
                break;
        }com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onCreate(android.os.Bundle)",this);
    }

    @Override
    public boolean onCreateOptionsMenu(Menu options) {
        com.mijack.Xlog.logMethodEnter("boolean com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onCreateOptionsMenu(android.view.Menu)",this);getMenuInflater().inflate(R.menu.activity_issue_filter, options);
        {com.mijack.Xlog.logMethodExit("boolean com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onCreateOptionsMenu(android.view.Menu)",this,1);return true;}
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        com.mijack.Xlog.logMethodEnter("boolean com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onOptionsItemSelected(android.view.MenuItem)",this);switch (item.getItemId()) {
            case R.id.m_apply:
                Intent intent = new Intent();
                intent.putExtra(EXTRA_ISSUE_FILTER, filter);
                setResult(RESULT_OK, intent);
                finish();
                {com.mijack.Xlog.logMethodExit("boolean com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onOptionsItemSelected(android.view.MenuItem)",this,1);return true;}
            default:
                {com.mijack.Xlog.logMethodExit("boolean com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onOptionsItemSelected(android.view.MenuItem)",this,2);return super.onOptionsItemSelected(item);}
        }
    }

    @Override
    protected void onSaveInstanceState(Bundle outState) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onSaveInstanceState(android.os.Bundle)",this);super.onSaveInstanceState(outState);

        outState.putParcelable(EXTRA_ISSUE_FILTER, filter);com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onSaveInstanceState(android.os.Bundle)",this);
    }

    @OnClick({R.id.tv_assignee, R.id.tv_assignee_label})
    protected void onAssigneeClicked() {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onAssigneeClicked()",this);if (assigneeDialog == null) {
            assigneeDialog = new AssigneeDialog(this, REQUEST_ASSIGNEE, filter.getRepository());
        }
        assigneeDialog.show(filter.getAssignee());com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onAssigneeClicked()",this);
    }

    @OnClick({R.id.tv_milestone, R.id.tv_milestone_label})
    protected void onMilestoneClicked() {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onMilestoneClicked()",this);if (milestoneDialog == null) {
            milestoneDialog = new MilestoneDialog(this, REQUEST_MILESTONE, filter.getRepository());
        }
        milestoneDialog.show(filter.getMilestone());com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onMilestoneClicked()",this);
    }

    @OnClick({R.id.tv_labels, R.id.tv_labels_label})
    protected void onLabelsClicked() {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onLabelsClicked()",this);if (labelsDialog == null) {
            labelsDialog = new LabelsDialog(this, REQUEST_LABELS, filter.getRepository());
        }
        labelsDialog.show(filter.getLabels());com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onLabelsClicked()",this);
    }

    private void onStatusChanged(RadioGroup radioGroup, int checkedId) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onStatusChanged(android.widget.RadioGroup,int)",this);filter.setOpen(checkedId == R.id.rb_open);com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onStatusChanged(android.widget.RadioGroup,int)",this);
    }

    private void onSortOrderChanged(RadioGroup radioGroup, int checkedId) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onSortOrderChanged(android.widget.RadioGroup,int)",this);if (checkedId == R.id.rb_asc) {
            filter.setDirection(IssueFilter.DIRECTION_ASCENDING);
        } else {
            filter.setDirection(IssueFilter.DIRECTION_DESCENDING);
        }com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onSortOrderChanged(android.widget.RadioGroup,int)",this);
    }

    private void onSortTypeChanged(RadioGroup radioGroup, int checkedId) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onSortTypeChanged(android.widget.RadioGroup,int)",this);switch (checkedId) {
            case R.id.rb_created:
                filter.setSortType(IssueFilter.SORT_CREATED);
                break;
            case R.id.rb_updated:
                filter.setSortType(IssueFilter.SORT_UPDATED);
                break;
            case R.id.rb_comments:
                filter.setSortType(IssueFilter.SORT_COMMENTS);
                break;
            default:
                break;
        }com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onSortTypeChanged(android.widget.RadioGroup,int)",this);
    }

    private void updateLabels() {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.updateLabels()",this);List<Label> selected = filter.getLabels();
        if (selected != null) {
            LabelDrawableSpan.setText(labelsText, selected);
        } else {
            labelsText.setText(R.string.none);
        }com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.updateLabels()",this);
    }

    private void updateMilestone() {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.updateMilestone()",this);Milestone selected = filter.getMilestone();
        if (selected != null) {
            milestoneText.setText(selected.title());
        } else {
            milestoneText.setText(R.string.none);
        }com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.updateMilestone()",this);
    }

    private void updateAssignee() {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.updateAssignee()",this);User selected = filter.getAssignee();
        if (selected != null) {
            avatars.bind(avatarView, selected);
            assigneeText.setText(selected.login());
        } else {
            avatarView.setVisibility(GONE);
            assigneeText.setText(R.string.assignee_anyone);
        }com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.updateAssignee()",this);
    }

    @Override
    public void onDialogResult(int requestCode, int resultCode, Bundle arguments) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onDialogResult(int,int,android.os.Bundle)",this);if (RESULT_OK != resultCode) {
            {com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.EditIssuesFilterActivity.onDialogResult(int,int,android.os.Bundle)",this,1);return;}
        }

        switch (requestCode) {
            case REQUEST_LABELS:
                filter.setLabels(LabelsDialogFragment.getSelected(arguments));
                updateLabels();
                break;
            case REQUEST_MILESTONE:
                filter.setMilestone(MilestoneDialogFragment.getSelected(arguments));
                updateMilestone();
                break;
            case REQUEST_ASSIGNEE:
                filter.setAssignee(AssigneeDialogFragment.getSelected(arguments));
                updateAssignee();
                break;
        }
    }
}
