    @Override
    public void onPageSelected(final int position) {
        com.mijack.Xlog.logMethodEnter("void com.github.pockethub.android.ui.issue.IssuesViewActivity.onPageSelected(int)",this);super.onPageSelected(position);

        if (repo != null) {
            updateTitle(position);
            {com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.IssuesViewActivity.onPageSelected(int)",this,1);return;}
        }

        if (repoIds == null) {
            {com.mijack.Xlog.logMethodExit("void com.github.pockethub.android.ui.issue.IssuesViewActivity.onPageSelected(int)",this,2);return;}
        }

        ActionBar actionBar = getSupportActionBar();
        repo = repoIds.get(position);
        if (repo != null) {
            updateTitle(position);
            actionBar.setSubtitle(InfoUtils.createRepoId(repo));
            Issue issue = store.getIssue(repo, issueNumbers[position]);
            if (issue != null) {
                Repository fullRepo = issue.repository();
                if (fullRepo != null && fullRepo.owner() != null) {
                    user = fullRepo.owner();
                    avatars.bind(actionBar, user);
                } else {
                    actionBar.setLogo(null);
                }
            } else {
                actionBar.setLogo(null);
            }
        } else {
            actionBar.setSubtitle(null);
            actionBar.setLogo(null);
        }
    }

